        let name = e.target.name;
        let val = e.target.value;
        setData(prevState => ({
            ...prevState,
            [name]: val,
        }));
    }
    const submitIt = ()=>{
        console.log(data);
    }
  return (
    <div id='Form'>
        <div className="box">
            <label htmlFor="Name">Name:</label>
            <input type="text" name='name' value={data.name} onChange={handleChange} placeholder='Name'/>
        </div>
        <div className="box">
            <label htmlFor="Gender">Gender:</label>
            <input type="text" name='gender' value={data.gender} onChange={handleChange} placeholder='Gender'/>
        </div>
        <div className="btn" onClick={submitIt}>Submit</div>
    </div>
  )
}

export default Form
