import "../styles/form.css";
const Login = () => {
    const [data,setData] = useState({name:"",gender:"male",email:"",address:"",message:"",phone:"",nationality:"India"});
    const handleChange = (e)=>{
        let name = e.target.name;
        let val = e.target.value;
        setData(prevState => ({
            ...prevState,
            [name]: val,
        }));
    }
    const submitIt = ()=>{
