    }
  return (
    <div id="Login">
      <div className="container">
        <div className="box">
          <label htmlFor="email">Email:</label>
          <input
            type="text"
            name="email"
            value={data.email}
            onChange={handleChange}
            placeholder="email"
          />
        </div>
        <div className="box">
          <label htmlFor="password">Password:</label>
          <input
            type="text"
            name="Password"
            value={data.password}
            onChange={handleChange}
            placeholder="123@gmail.com"
          />
        </div>
      </div>
    </div>
  );
};

export default Login;
